SELECT r.case_id,r.reporter_phone,r.reporter_name,r.danger_address_province,r.danger_address_city,r.detail_address,r.createtime,r.danger_date,r.danger_cause,r.danger_type,r.case_status,r.danger_cause, w.car_number FROM case_info r LEFT JOIN warranty_info w ON r.warranty_id=w.warranty_id
r.case_status='定损中'
and r.case_id=#{reported_number}

